def check(x):
    text = '' if x is None else str(x)
    expletives = ["damn", "hell", "shit", "fuck", "bitch", "asshole", "crap", "piss", "twat", "cunt"]
    return any(term in text.lower() for term in expletives)

def check(x):
    text = '' if x is None else str(x)
    informal_terms = ["lol", "brb", "omg", "wtf", "idk", "bff", "lmao", "smh", "fml", "yolo"]
    expletives = ["damn", "hell", "shit", "fuck", "bitch", "asshole", "crap", "piss", "twat", "cunt"]
    return any(term in text.lower() for term in informal_terms) or any(term in text.lower() for term in expletives)
